import React, { useState, useEffect } from 'react';
import { XIcon, UploadIcon } from 'lucide-react';
import { toast } from 'sonner';
import api from '../../services/api';

const CATEGORY_CHOICES = [
  { value: 'GAMES', label: 'Games' },
  { value: 'PRODUCTIVITY', label: 'Productivity' },
  { value: 'SOCIAL', label: 'Social Media' },
  { value: 'UTILITY', label: 'Utility' },
  { value: 'OTHER', label: 'Other' }
];

const AddAppModal = ({ isOpen, onClose, app, onAppAdded }) => {
  // Initial form state
  const initialFormState = {
    name: '',
    description: '',
    package_name: '',
    points_value: 10,
    category: 'OTHER',
    app_icon: null,
    playstore_link: '',
    is_active: true
  };

  // State management
  const [formData, setFormData] = useState(initialFormState);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewIcon, setPreviewIcon] = useState(null);

  // Reset form when modal opens or app changes
  useEffect(() => {
    if (app) {
      // Editing existing app
      setFormData({
        id: app.id,
        name: app.name,
        description: app.description || '',
        package_name: app.package_name,
        points_value: app.points_value || 10,
        category: app.category || 'OTHER',
        playstore_link: app.playstore_link || '',
        is_active: app.is_active !== undefined ? app.is_active : true
      });
      setSelectedFile(null);
      setPreviewIcon(app.app_icon || null);
    } else {
      // Adding new app
      resetForm();
    }
  }, [app, isOpen]);

  // Reset form to initial state
  const resetForm = () => {
    setFormData(initialFormState);
    setSelectedFile(null);
    setPreviewIcon(null);
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      
      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewIcon(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation
    if (!formData.name || !formData.package_name) {
      toast.error('Name and Package Name are required');
      return;
    }

    const submitData = new FormData();
    
    // Append all form data
    Object.keys(formData).forEach(key => {
      if (formData[key] !== null && formData[key] !== '') {
        submitData.append(key, formData[key]);
      }
    });

    // Append file if selected
    if (selectedFile) {
      submitData.append('app_icon', selectedFile);
    }

    try {
      if (app) {
        // Update existing app
        await api.put(`/apps/${app.id}/`, submitData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App updated successfully');
      } else {
        // Create new app
        await api.post('/apps/', submitData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App added successfully');
      }
      
      // Reset and close
      onAppAdded();
      onClose();
      resetForm();
    } catch (error) {
      console.error('Error submitting app:', error);
      toast.error(error.response?.data?.message || 'Failed to submit app');
    }
  };

  // Render nothing if modal is closed
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-gray-800 rounded-lg shadow-xl p-6 w-[600px] max-h-[90vh] overflow-y-auto">
        {/* Modal Header */}
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-200">
            {app ? 'Edit App' : 'Add New App'}
          </h2>
          <button 
            onClick={() => {
              onClose();
              resetForm();
            }}
            className="text-gray-400 hover:text-gray-200 transition duration-300"
          >
            <XIcon className="w-6 h-6" />
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-5">
          {/* App Icon Upload */}
          <div className="flex items-center space-x-4">
            <div className="w-24 h-24 bg-gray-700 rounded-lg flex items-center justify-center">
              {previewIcon ? (
                <img 
                  src={previewIcon} 
                  alt="App Icon" 
                  className="w-full h-full object-cover rounded-lg"
                />
              ) : (
                <UploadIcon className="text-gray-500 w-12 h-12" />
              )}
            </div>
            <div>
              <input
                type="file"
                id="app_icon"
                name="app_icon"
                onChange={handleFileChange}
                accept="image/*"
                className="hidden"
              />
              <label 
                htmlFor="app_icon" 
                className="cursor-pointer bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded transition duration-300"
              >
                {previewIcon ? 'Change Icon' : 'Upload Icon'}
              </label>
            </div>
          </div>

          {/* App Name */}
          <div>
            <label className="block text-gray-300 mb-2 font-medium">App Name</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              placeholder="Enter app name"
              className="w-full px-3 py-2 bg-gray-700 text-gray-200 rounded focus:ring-2 focus:ring-blue-500 transition duration-300"
            />
          </div>

          {/* Description */}
          <div>
            <label className="block text-gray-300 mb-2 font-medium">Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Enter app description"
              className="w-full px-3 py-2 bg-gray-700 text-gray-200 rounded focus:ring-2 focus:ring-blue-500 transition duration-300"
              rows="3"
            />
          </div>

          {/* Package Name */}
          <div>
            <label className="block text-gray-300 mb-2 font-medium">Package Name</label>
            <input
              type="text"
              name="package_name"
              value={formData.package_name}
              onChange={handleChange}
              required
              placeholder="com.example.appname"
              className="w-full px-3 py-2 bg-gray-700 text-gray-200 rounded focus:ring-2 focus:ring-blue-500 transition duration-300"
            />
          </div>

          {/* Points Value */}
          <div>
            <label className="block text-gray-300 mb-2 font-medium">Points Value</label>
            <input
              type="number"
              name="points_value"
              value={formData.points_value}
              onChange={handleChange}
              min="1"
              max="1000"
              placeholder="Enter points"
              className="w-full px-3 py-2 bg-gray-700 text-gray-200 rounded focus:ring-2 focus:ring-blue-500 transition duration-300"
            />
          </div>

          {/* Category */}
          <div>
            <label className="block text-gray-300 mb-2 font-medium">Category</label>
            <select
              name="category"
              value={formData.category}
              onChange={handleChange}
              className="w-full px-3 py-2 bg-gray-700 text-gray-200 rounded focus:ring-2 focus:ring-blue-500 transition duration-300"
            >
              {CATEGORY_CHOICES.map((cat) => (
                <option key={cat.value} value={cat.value}>
                  {cat.label}
                </option>
              ))}
            </select>
          </div>

          {/* Play Store Link */}
          <div>
            <label className="block text-gray-300 mb-2 font-medium">Play Store Link</label>
            <input
              type="url"
              name="playstore_link"
              value={formData.playstore_link}
              onChange={handleChange}
              placeholder="https://play.google.com/store/apps/details?id=..."
              className="w-full px-3 py-2 bg-gray-700 text-gray-200 rounded focus:ring-2 focus:ring-blue-500 transition duration-300"
            />
          </div>

          {/* Active Status */}
          <div className="flex items-center">
            <input
              type="checkbox"
              name="is_active"
              checked={formData.is_active}
              onChange={handleChange}
              className="mr-2 text-blue-500 bg-gray-700 border-gray-600 rounded focus:ring-blue-500"
            />
            <label className="text-gray-300">Active App</label>
          </div>

          {/* Submit Buttons */}
          <div className="flex space-x-4 pt-4">
            <button
              type="submit"
              className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded transition duration-300 flex-grow"
            >
              {app ? 'Update App' : 'Add App'}
            </button>
            <button
              type="button"
              onClick={() => {
                onClose();
                resetForm();
              }}
              className="bg-gray-700 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded transition duration-300 flex-grow"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddAppModal;
