import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Toaster, toast } from "sonner";

const Register = () => {
  const baseURL = "http://127.0.0.1:8000/api";

  const [formData, setFormData] = useState({
    email: "",
    first_name: "",
    last_name: "",
    password: "",
    password2: "",
    is_manager: false,
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const { email, first_name, last_name, password, password2, is_manager } = formData;

  // Existing validation and handlers remain the same
  const validateForm = () => {
    const newErrors = {};
    if (password !== password2) {
      newErrors.password2 = "Passwords do not match";
    }
    if (password.length < 8) {
      newErrors.password = "Password must be at least 8 characters long";
    }
    if (!email.includes('@')) {
      newErrors.email = "Please enter a valid email address";
    }
    if (!first_name.trim()) {
      newErrors.first_name = "First name is required";
    }
    if (!last_name.trim()) {
      newErrors.last_name = "Last name is required";
    }
    return newErrors;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: type === "checkbox" ? checked : value,
    }));

    if (errors[name]) {
      setErrors((prev) => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors({});

    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      toast.error("Please fix the form errors");
      return;
    }

    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        withCredentials: true
      };

      const submitData = {
        email: email.trim().toLowerCase(),
        first_name: first_name.trim(),
        last_name: last_name.trim(),
        password: password,
        password2: password2,
        is_manager: is_manager
      };

      const response = await axios.post(
        `${baseURL}/register/`, 
        submitData,
        config
      );
      
      toast.success("Registration successful! Redirecting to login...");
      setTimeout(() => {
        navigate("/login");
      }, 2000);
    } catch (err) {
      if (err.response) {
        const serverErrors = err.response.data;
        if (typeof serverErrors === 'string') {
          toast.error(serverErrors);
          setErrors({ detail: serverErrors });
        } else if (typeof serverErrors === 'object') {
          setErrors(serverErrors);
          Object.entries(serverErrors).forEach(([key, value]) => {
            const errorMessage = Array.isArray(value) ? value[0] : value;
            toast.error(`${key}: ${errorMessage}`);
          });
        }
      } else if (err.request) {
        toast.error("Unable to connect to the server. Please try again.");
        setErrors({ detail: "Network error: Unable to connect to the server" });
      } else {
        toast.error("An unexpected error occurred");
        setErrors({ detail: "An unexpected error occurred" });
      }
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-50 to-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8 bg-white rounded-2xl shadow-2xl p-8 transform transition-all duration-300 hover:scale-[1.01]">
        <div>
          <h2 className="mt-4 text-center text-3xl font-extrabold text-gray-900 tracking-tight">
            Create Your Account
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Join us to start managing your leave applications
          </p>
        </div>

        <form onSubmit={handleSubmit} className="mt-8 space-y-6">
          <div className="grid grid-cols-1 gap-6 md:grid-cols-2">
            <div className="relative">
              <label
                htmlFor="first_name"
                className="text-sm font-medium text-gray-700 block mb-2"
              >
                First Name
              </label>
              <input
                id="first_name"
                name="first_name"
                type="text"
                value={first_name}
                onChange={handleChange}
                autoComplete="given-name"
                required
                className={`appearance-none relative block w-full px-3 py-2.5 border ${
                  errors.first_name ? 'border-red-500' : 'border-gray-300'
                } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent sm:text-sm transition-all duration-300 ease-in-out`}
              />
              {errors.first_name && (
                <p className="mt-1 text-red-500 text-xs">{errors.first_name}</p>
              )}
            </div>

            <div className="relative">
              <label
                htmlFor="last_name"
                className="text-sm font-medium text-gray-700 block mb-2"
              >
                Last Name
              </label>
              <input
                id="last_name"
                name="last_name"
                type="text"
                value={last_name}
                onChange={handleChange}
                autoComplete="family-name"
                required
                className={`appearance-none relative block w-full px-3 py-2.5 border ${
                  errors.last_name ? 'border-red-500' : 'border-gray-300'
                } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent sm:text-sm transition-all duration-300 ease-in-out`}
              />
              {errors.last_name && (
                <p className="mt-1 text-red-500 text-xs">{errors.last_name}</p>
              )}
            </div>
          </div>

          <div className="space-y-6">
            <div className="relative">
              <label
                htmlFor="email"
                className="text-sm font-medium text-gray-700 block mb-2"
              >
                Email address
              </label>
              <input
                id="email"
                name="email"
                type="email"
                value={email}
                onChange={handleChange}
                autoComplete="email"
                required
                className={`appearance-none relative block w-full px-3 py-2.5 border ${
                  errors.email ? 'border-red-500' : 'border-gray-300'
                } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent sm:text-sm transition-all duration-300 ease-in-out`}
                placeholder="name@company.com"
              />
              {errors.email && (
                <p className="mt-1 text-red-500 text-xs">{errors.email}</p>
              )}
            </div>

            <div className="relative">
              <label
                htmlFor="password"
                className="text-sm font-medium text-gray-700 block mb-2"
              >
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                value={password}
                onChange={handleChange}
                autoComplete="new-password"
                required
                className={`appearance-none relative block w-full px-3 py-2.5 border ${
                  errors.password ? 'border-red-500' : 'border-gray-300'
                } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent sm:text-sm transition-all duration-300 ease-in-out`}
              />
              {errors.password && (
                <p className="mt-1 text-red-500 text-xs">{errors.password}</p>
              )}
            </div>

            <div className="relative">
              <label
                htmlFor="password2"
                className="text-sm font-medium text-gray-700 block mb-2"
              >
                Confirm Password
              </label>
              <input
                id="password2"
                name="password2"
                type="password"
                value={password2}
                onChange={handleChange}
                autoComplete="new-password"
                required
                className={`appearance-none relative block w-full px-3 py-2.5 border ${
                  errors.password2 ? 'border-red-500' : 'border-gray-300'
                } placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent sm:text-sm transition-all duration-300 ease-in-out`}
              />
              {errors.password2 && (
                <p className="mt-1 text-red-500 text-xs">{errors.password2}</p>
              )}
            </div>
          </div>

          <div>
            <button
              type="submit"
              className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-lg text-white bg-gradient-to-r from-blue-500 via-blue-600 to-blue-700 hover:from-blue-600 hover:via-blue-700 hover:to-blue-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transform transition-all duration-300 ease-in-out hover:scale-[1.02] shadow-lg hover:shadow-xl"
            >
              Create account
            </button>
          </div>

          {errors.detail && (
            <p className="text-red-500 text-sm text-center mt-2">{errors.detail}</p>
          )}

          <p className="text-center text-sm text-gray-600">
            Already have an account?{" "}
            <Link
              to="/login"
              className="font-medium text-blue-600 hover:text-blue-500 transition-colors duration-300"
            >
              Sign in
            </Link>
          </p>
        </form>
      </div>
      <Toaster position="top-right" />
    </div>
  );
};

export default Register;
