import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { toast } from 'sonner';
import api from '../../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const firstName = useSelector((state) => state.auth.first_name);
  
  // State for apps
  const [apps, setApps] = useState([]);
  const [pendingApps, setPendingApps] = useState([]);
  const [activeView, setActiveView] = useState('all');

  // Fetch apps on component mount
  useEffect(() => {
    fetchApps();
    fetchPendingApps();
  }, []);

  // Fetch all apps from backend
  const fetchApps = async () => {
    try {
      const response = await api.get('apps/');
      setApps(response.data);
    } catch (error) {
      console.error('Error fetching apps:', error);
      toast.error('Failed to fetch apps');
    }
  };

  // Fetch pending apps
  const fetchPendingApps = async () => {
    try {
      const response = await api.get('apps/pending/');
      setPendingApps(response.data);
    } catch (error) {
      console.error('Error fetching pending apps:', error);
      toast.error('Failed to fetch pending apps');
    }
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  // Navigate to add app page
  const handleAddApp = () => {
    navigate('/add-app');
  };

  // Approve pending app
  const handleApproveApp = async (appId) => {
    try {
      await api.post(`apps/${appId}/approve/`);
      toast.success('App approved successfully');
      fetchPendingApps();
      fetchApps();
    } catch (error) {
      console.error('Error approving app:', error);
      toast.error('Failed to approve app');
    }
  };

  // Reject pending app
  const handleRejectApp = async (appId) => {
    try {
      await api.post(`apps/${appId}/reject/`);
      toast.success('App rejected successfully');
      fetchPendingApps();
    } catch (error) {
      console.error('Error rejecting app:', error);
      toast.error('Failed to reject app');
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Navigation */}
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <button 
              onClick={handleAddApp}
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
            >
              Add New App
            </button>
            <button 
              onClick={() => setActiveView(activeView === 'all' ? 'pending' : 'all')}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              {activeView === 'all' ? 'View Pending Apps' : 'View All Apps'}
            </button>
          </div>
          <div className="flex items-center space-x-4 ml-auto">
            <h1 className="text-gray-300 text-lg">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-4 py-2 rounded text-gray-200 hover:bg-gray-700 transition duration-300"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      {/* Apps List */}
      <div className="container mx-auto mt-6 px-6">
        <h2 className="text-2xl font-bold mb-4">
          {activeView === 'all' ? 'All Apps' : 'Pending Apps'}
        </h2>
        
        {activeView === 'all' ? (
          <table className="w-full">
            <thead>
              <tr className="bg-gray-800 text-gray-300">
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Points</th>
              </tr>
            </thead>
            <tbody>
              {apps.map((app) => (
                <tr key={app.id} className="border-b border-gray-800">
                  <td className="border px-4 py-2 text-gray-200">{app.name}</td>
                  <td className="border px-4 py-2 text-gray-200">{app.category}</td>
                  <td className="border px-4 py-2 text-gray-200">{app.points_value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <table className="w-full">
            <thead>
              <tr className="bg-gray-800 text-gray-300">
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Package Name</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {pendingApps.map((app) => (
                <tr key={app.id} className="border-b border-gray-800">
                  <td className="border px-4 py-2 text-gray-200">{app.name}</td>
                  <td className="border px-4 py-2 text-gray-200">{app.category}</td>
                  <td className="border px-4 py-2 text-gray-200">{app.package_name}</td>
                  <td className="border px-4 py-2">
                    <button
                      onClick={() => handleApproveApp(app.id)}
                      className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-2"
                    >
                      Approve
                    </button>
                    <button
                      onClick={() => handleRejectApp(app.id)}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                    >
                      Reject
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
