import React, { useState, useEffect } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";

const ManagerLeaveReport = () => {
  const [report, setReport] = useState([]);
  const baseURL = "http://127.0.0.1:8000";

  useEffect(() => {
    const fetchReport = async () => {
      try {
        const token = localStorage.getItem("access");
        const response = await axios.get(baseURL + "/leave/manager/report/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setReport(response.data.report);
      } catch (error) {
        console.error("Error fetching report:", error);
      }
    };

    fetchReport();
  }, []);

  const downloadPDF = () => {
    const doc = new jsPDF();

    // Add title
    doc.setFontSize(16);
    doc.text("Employee Leave Report", 14, 20);
    doc.setFontSize(12);
    doc.text(`Generated on: ${new Date().toLocaleDateString()}`, 14, 30);

    // Check if there is data to display
    if (report.length > 0) {
      // Define table headers
      const headers = [
        ["Employee", "Leave Type", "Date", "Reason", "Status"]
      ];

      // Define table rows
      const data = report.map((leave) => [
        leave.employee,
        leave.leave_type.charAt(0).toUpperCase() + leave.leave_type.slice(1),
        new Date(leave.date).toLocaleDateString(),
        leave.reason,
        leave.status.charAt(0).toUpperCase() + leave.status.slice(1)
      ]);

      // Add table to PDF using autoTable
      doc.autoTable({
        head: headers,
        body: data,
        startY: 40,
        styles: {
          fontSize: 10,
          cellPadding: 3
        },
        headStyles: {
          fillColor: [41, 128, 185],
          textColor: 255
        },
        alternateRowStyles: {
          fillColor: [245, 245, 245]
        }
      });

      // Save the PDF
      doc.save("employee-leave-report.pdf");
    } else {
      // If no leave history found, show a message in the PDF
      doc.text("No leave history available.", 14, 40);
      doc.save("employee-leave-report.pdf");
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-semibold">All Employees Leave Report</h2>
        <button 
          onClick={downloadPDF}
          className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 flex items-center gap-2"
        >
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            className="h-5 w-5" 
            viewBox="0 0 24 24" 
            fill="none" 
            stroke="currentColor" 
            strokeWidth="2" 
            strokeLinecap="round" 
            strokeLinejoin="round"
          >
            <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" />
            <polyline points="7 10 12 15 17 10" />
            <line x1="12" y1="15" x2="12" y2="3" />
          </svg>
          Download PDF
        </button>
      </div>
      
      {report.length ? (
        <div className="overflow-x-auto">
          <table className="min-w-full border-collapse border border-gray-200">
            <thead>
              <tr className="bg-gray-100">
                <th className="p-3 text-left border border-gray-200 font-semibold">Employee</th>
                <th className="p-3 text-left border border-gray-200 font-semibold">Leave Type</th>
                <th className="p-3 text-left border border-gray-200 font-semibold">Date</th>
                <th className="p-3 text-left border border-gray-200 font-semibold">Reason</th>
                <th className="p-3 text-left border border-gray-200 font-semibold">Status</th>
              </tr>
            </thead>
            <tbody>
              {report.map((leave, index) => (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="p-3 border border-gray-200">{leave.employee}</td>
                  <td className="p-3 border border-gray-200">
                    {leave.leave_type.charAt(0).toUpperCase() + 
                     leave.leave_type.slice(1)}
                  </td>
                  <td className="p-3 border border-gray-200">
                    {new Date(leave.date).toLocaleDateString()}
                  </td>
                  <td className="p-3 border border-gray-200">{leave.reason}</td>
                  <td className="p-3 border border-gray-200">
                    {leave.status.charAt(0).toUpperCase() + 
                     leave.status.slice(1)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center py-4 text-gray-600">No leave data available</p>
      )}
    </div>
  );
};

export default ManagerLeaveReport;
