import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Toaster, toast } from "sonner";

const Register = () => {
  const baseURL = "http://127.0.0.1:8000/api";  // Updated URL structure

  const [formData, setFormData] = useState({
    email: "",
    first_name: "",
    last_name: "",
    password: "",
    password2: "",
    is_manager: false,
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const { email, first_name, last_name, password, password2, is_manager } = formData;

  // Client-side validation
  const validateForm = () => {
    const newErrors = {};
    
    if (password !== password2) {
      newErrors.password2 = "Passwords do not match";
    }
    
    if (password.length < 8) {
      newErrors.password = "Password must be at least 8 characters long";
    }
    
    if (!email.includes('@')) {
      newErrors.email = "Please enter a valid email address";
    }

    if (!first_name.trim()) {
      newErrors.first_name = "First name is required";
    }

    if (!last_name.trim()) {
      newErrors.last_name = "Last name is required";
    }

    return newErrors;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
    // Clear error for the field being changed
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Clear previous errors
    setErrors({});

    // Perform client-side validation
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      toast.error("Please fix the form errors");
      return;
    }

    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        withCredentials: true
      };

      // Clean up the data before sending
      const submitData = {
        email: email.trim(),
        first_name: first_name.trim(),
        last_name: last_name.trim(),
        password: password,
        password2: password2,
        is_manager: is_manager
      };

      console.log('Submitting data:', submitData); // For debugging

      const response = await axios.post(
        `${baseURL}/register/`, 
        submitData,
        config
      );

      console.log('Server response:', response.data); // For debugging

      toast.success("Registration successful! Redirecting to login...");
      setTimeout(() => {
        navigate("/login");
      }, 2000);
    } catch (err) {
      console.error('Registration error:', err); // For debugging

      if (err.response) {
        const serverErrors = err.response.data;
        setErrors(serverErrors);
        
        // Display the first error message in a toast
        const firstError = Object.values(serverErrors)[0];
        toast.error(Array.isArray(firstError) ? firstError[0] : firstError);
      } else if (err.request) {
        toast.error("Unable to connect to the server. Please try again.");
        setErrors({ detail: "Network error: Unable to connect to the server" });
      } else {
        toast.error("An unexpected error occurred");
        setErrors({ detail: "An unexpected error occurred" });
      }
    }
  };

  return (
    <div className="flex min-h-full flex-col justify-center px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-sm">
        <h2 className="mt-10 text-center text-3xl font-bold leading-9 tracking-tight text-gray-900">
          Sign Up
        </h2>
      </div>

      <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label
              htmlFor="first_name"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              First Name
            </label>
            <input
              id="first_name"
              name="first_name"
              type="text"
              value={first_name}
              onChange={handleChange}
              autoComplete="given-name"
              required
              className={`bg-gray-50 border ${
                errors.first_name ? 'border-red-500' : 'border-gray-300'
              } text-black rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5`}
            />
            {errors.first_name && (
              <p className="mt-1 text-red-500 text-sm">{errors.first_name}</p>
            )}
          </div>

          <div>
            <label
              htmlFor="last_name"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Last Name
            </label>
            <input
              id="last_name"
              name="last_name"
              type="text"
              value={last_name}
              onChange={handleChange}
              autoComplete="family-name"
              required
              className={`bg-gray-50 border ${
                errors.last_name ? 'border-red-500' : 'border-gray-300'
              } text-black rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5`}
            />
            {errors.last_name && (
              <p className="mt-1 text-red-500 text-sm">{errors.last_name}</p>
            )}
          </div>

          <div>
            <label
              htmlFor="email"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Email address
            </label>
            <input
              id="email"
              name="email"
              type="email"
              value={email}
              onChange={handleChange}
              autoComplete="email"
              required
              className={`bg-gray-50 border ${
                errors.email ? 'border-red-500' : 'border-gray-300'
              } text-black rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5`}
              placeholder="name@company.com"
            />
            {errors.email && (
              <p className="mt-1 text-red-500 text-sm">{errors.email}</p>
            )}
          </div>

          <div>
            <label
              htmlFor="password"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Password
            </label>
            <input
              id="password"
              name="password"
              type="password"
              value={password}
              onChange={handleChange}
              autoComplete="new-password"
              required
              className={`bg-gray-50 border ${
                errors.password ? 'border-red-500' : 'border-gray-300'
              } text-black rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5`}
            />
            {errors.password && (
              <p className="mt-1 text-red-500 text-sm">{errors.password}</p>
            )}
          </div>

          <div>
            <label
              htmlFor="password2"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Confirm Password
            </label>
            <input
              id="password2"
              name="password2"
              type="password"
              value={password2}
              onChange={handleChange}
              autoComplete="new-password"
              required
              className={`bg-gray-50 border ${
                errors.password2 ? 'border-red-500' : 'border-gray-300'
              } text-black rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5`}
            />
            {errors.password2 && (
              <p className="mt-1 text-red-500 text-sm">{errors.password2}</p>
            )}
          </div>

          <div>
            <button
              type="submit"
              className="flex w-full justify-center rounded-md bg-gradient-to-r from-blue-500 via-blue-600 to-blue-700 hover:bg-gradient-to-br px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
            >
              Create account
            </button>
          </div>

          {errors.detail && (
            <p className="text-red-500 text-sm text-center">{errors.detail}</p>
          )}

          <p className="mt-10 text-center text-sm text-gray-500">
            Already have an account?{" "}
            <Link
              to="/login"
              className="font-semibold leading-6 text-indigo-600 hover:text-indigo-500"
            >
              Sign in
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Register;
