import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "sonner";

const ApplyLeaveModal = ({ closeModal }) => {
const baseURL = "http://127.0.0.1:8000";
  const navigate = useNavigate();
  const [leaveData, setLeaveData] = useState({
    leave_type: "",
    start_date: "",
    end_date: "",
    reason: "",
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    setLeaveData({ ...leaveData, [e.target.name]: e.target.value });
  };

  const submitLeaveRequest = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("access");

    try {
      const response = await axios.post(baseURL + "/leave/apply/", leaveData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.status === 201) {
        toast.success("Leave request submitted successfully!");
        closeModal();
        navigate("/dashboard");
      }
    } catch (error) {
      if (error.response && error.response.data) {
        setErrors(error.response.data);
      }
      const errorMessage =
        error.response?.data?.message ||
        error.message ||
        "Something went wrong!";
      toast.error(errorMessage);
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white p-6 rounded-md shadow-lg w-1/3 relative">
        {/* Close button (X) */}
        <button
          onClick={closeModal}
          className="absolute top-2 right-2 text-gray-600 hover:text-red-500"
        >
          &times;
        </button>

        <h2 className="text-xl font-bold mb-4">Apply for Leave</h2>
        <form onSubmit={submitLeaveRequest} className="space-y-4">
          <div>
            <label
              htmlFor="leave_type"
              className="block text-sm font-medium text-gray-700"
            >
              Leave Type
            </label>
            <select
              id="leave_type"
              name="leave_type"
              value={leaveData.leave_type}
              onChange={handleInputChange}
              className="mt-1 block w-full p-2 border rounded-md shadow-sm"
            >
              <option value="">Select Leave Type</option>
              <option value="annual">Annual Leave</option>
              <option value="sick">Sick Leave</option>
              <option value="casual">Casual Leave</option>
              <option value="maternity">Maternity Leave</option>
            </select>
          </div>
          {errors.leave_type && (
            <span className="text-red-600">{errors.leave_type[0]}</span>
          )}
          <div>
            <label
              htmlFor="startdate"
              className="block text-sm font-medium text-gray-700"
            >
              Start Date
            </label>
            <input
              type="date"
              id="start_date"
              name="start_date"
              value={leaveData.start_date}
              onChange={handleInputChange}
              className="mt-1 block w-full p-2 border rounded-md shadow-sm"
              min={new Date().toISOString().split("T")[0]}
            />
          </div>
          {errors.startdate && (
            <span className="text-red-600">{errors.start_date[0]}</span>
          )}
          <div>
            <label
              htmlFor="enddate"
              className="block text-sm font-medium text-gray-700"
            >
              End Date
            </label>
            <input
              type="date"
              id="end_date"
              name="end_date"
              value={leaveData.end_date}
              onChange={handleInputChange}
              className="mt-1 block w-full p-2 border rounded-md shadow-sm"
              min={new Date().toISOString().split("T")[0]}
            />
          </div>
          {errors.end_date && (
            <span className="text-red-600">{errors.end_date[0]}</span>
          )}
          {/* Ensure enddate is after startdate */}
          {leaveData.start_date &&
            leaveData.end_date &&
            leaveData.end_date < leaveData.start_date && (
              <span className="text-red-600">
                End date must be after start date.
              </span>
            )}
          <div>
            <label
              htmlFor="reason"
              className="block text-sm font-medium text-gray-700"
            >
              Reason
            </label>
            <textarea
              id="reason"
              name="reason"
              value={leaveData.reason}
              onChange={handleInputChange}
              className="mt-1 block w-full p-2 border rounded-md shadow-sm"
            />
          </div>
          {errors.reason && (
            <span className="text-red-600">{errors.reason[0]}</span>
          )}
          <button
            type="submit"
            className="w-full py-2 px-4 bg-green-600 text-white rounded-md hover:bg-green-700"
          >
            Submit Leave Request
          </button>
          <button
            type="button"
            onClick={closeModal}
            className="w-full py-2 px-4 mt-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
          >
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
};

export default ApplyLeaveModal;
