import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { toast } from 'sonner';
import api from '../../services/api';

// Modal Components
const Modal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto outline-none focus:outline-none">
      <div className="relative w-auto max-w-3xl mx-auto my-6">
        <div className="relative flex flex-col w-full bg-gray-800 border-0 rounded-lg shadow-lg outline-none focus:outline-none">
          {children}
          <div className="flex items-center justify-end p-6 border-t border-solid rounded-b border-gray-700">
            <button
              className="px-6 py-2 mb-1 mr-1 text-sm font-bold text-white uppercase transition-all duration-150 ease-linear bg-gray-600 rounded shadow outline-none hover:bg-gray-700 hover:shadow-lg focus:outline-none"
              type="button"
              onClick={onClose}
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

const Dashboard = () => {
  const navigate = useNavigate();
  const firstName = useSelector((state) => state.auth.first_name);
  
  // State for apps and form
  const [apps, setApps] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    package_name: '',
    points_value: 10,
    category: 'OTHER',
    app_icon: null,
    playstore_link: '',
    is_active: true
  });
  const [isEditing, setIsEditing] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  
  // Modal states
  const [isAddAppModalOpen, setIsAddAppModalOpen] = useState(false);
  const [isPendingApprovalModalOpen, setIsPendingApprovalModalOpen] = useState(false);

  // Category options
  const CATEGORY_CHOICES = [
    { value: 'GAMES', label: 'Games' },
    { value: 'PRODUCTIVITY', label: 'Productivity' },
    { value: 'SOCIAL', label: 'Social Media' },
    { value: 'UTILITY', label: 'Utility' },
    { value: 'OTHER', label: 'Other' }
  ];

  // Fetch apps on component mount
  useEffect(() => {
    fetchApps();
  }, []);

  // Fetch apps from backend
  const fetchApps = async () => {
    try {
      const response = await api.get('apps/');
      setApps(response.data);
    } catch (error) {
      console.error('Error fetching apps:', error);
      toast.error('Failed to fetch apps');
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setFormData(prev => ({
      ...prev,
      app_icon: file
    }));
  };

  // Submit form for add/edit
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const appData = new FormData();
    Object.keys(formData).forEach(key => {
      if (formData[key] !== null && formData[key] !== '') {
        if (key === 'app_icon') {
          if (selectedFile) {
            appData.append(key, formData[key]);
          }
        } else {
          appData.append(key, formData[key]);
        }
      }
    });

    try {
      if (isEditing) {
        await api.put(`apps/${formData.id}/update/`, appData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App updated successfully');
      } else {
        await api.post('apps/upload/', appData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App added successfully');
      }
      
      resetForm();
      fetchApps();
      setIsAddAppModalOpen(false);
    } catch (error) {
      console.error('Error submitting app:', error);
      
      if (error.response) {
        toast.error(error.response.data.message || 'Submission failed');
      } else if (error.request) {
        toast.error('No response received from server');
      } else {
        toast.error('Error: ' + error.message);
      }
    }
  };

  // Prepare app for editing
  const handleEdit = (app) => {
    setFormData(app);
    setIsEditing(true);
    setIsAddAppModalOpen(true);
  };

  // Delete app
  const handleDelete = async (appId) => {
    if (window.confirm('Are you sure you want to delete this app?')) {
      try {
        await api.delete(`apps/${appId}/delete/`);
        toast.success('App deleted successfully');
        fetchApps();
      } catch (error) {
        console.error('Error deleting app:', error);
        toast.error('Failed to delete app');
      }
    }
  };

  // Reset form
  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      package_name: '',
      points_value: 10,
      category: 'OTHER',
      app_icon: null,
      playstore_link: '',
      is_active: true
    });
    setSelectedFile(null);
    setIsEditing(false);
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-900">
      {/* Navbar */}
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setIsAddAppModalOpen(true)}
              className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded transition duration-300"
            >
              Add New App
            </button>
            <button
              onClick={() => setIsPendingApprovalModalOpen(true)}
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300"
            >
              Pending Approval
            </button>
          </div>
          <div className="flex items-center space-x-4 ml-auto">
            <h1 className="text-gray-300 text-lg">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-4 py-2 rounded text-gray-200 hover:bg-gray-700 transition duration-300"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      {/* Apps List */}
      <div className="container mx-auto mt-6 px-6">
        <h2 className="text-2xl font-bold mb-4 text-gray-200">Existing Apps</h2>
        <table className="w-full bg-gray-800 rounded-lg overflow-hidden shadow-lg">
          <thead>
            <tr className="bg-gray-700 text-gray-300">
              <th className="px-4 py-3 text-left">Name</th>
              <th className="px-4 py-3 text-left">Category</th>
              <th className="px-4 py-3 text-left">Points</th>
              <th className="px-4 py-3 text-center">Actions</th>
            </tr>
          </thead>
          <tbody>
            {apps.map((app) => (
              <tr key={app.id} className="border-b border-gray-700 hover:bg-gray-700 transition duration-200">
                <td className="px-4 py-3 text-gray-200">{app.name}</td>
                <td className="px-4 py-3 text-gray-200">{app.category}</td>
                <td className="px-4 py-3 text-gray-200">{app.points_value}</td>
                <td className="px-4 py-3 text-center">
                  <button
                    onClick={() => handleEdit(app)}
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(app.id)}
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Add App Modal */}
      <Modal 
        isOpen={isAddAppModalOpen} 
        onClose={() => {
          setIsAddAppModalOpen(false);
          resetForm();
        }}
      >
        <div className="relative p-6 flex-auto">
          <h2 className="text-2xl font-bold mb-4 text-gray-200">
            {isEditing ? 'Edit App' : 'Add New App'}
          </h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Form fields (same as before) */}
            {/* ... [All previous form fields] ... */}
            <div className="flex space-x-4">
              <button
                type="submit"
                className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
              >
                {isEditing ? 'Update App' : 'Add App'}
              </button>
            </div>
          </form>
        </div>
      </Modal>

      {/* Pending Approval Modal */}
      <Modal 
        isOpen={isPendingApprovalModalOpen} 
        onClose={() => setIsPendingApprovalModalOpen(false)}
      >
        <div className="relative p-6 flex-auto">
          <h2 className="text-2xl font-bold mb-4 text-gray-200">
            Pending Approval Apps
          </h2>
          {/* Add pending approval list or content here */}
          <p className="text-gray-400">No pending apps at the moment.</p>
        </div>
      </Modal>
    </div>
  );
};

export default Dashboard;
