import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'sonner';
import api from '../../services/api';

const AddApp = () => {
  const navigate = useNavigate();
  
  // Form data state
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    package_name: '',
    points_value: 10,
    category: 'OTHER',
    app_icon: null,
    playstore_link: '',
    is_active: true
  });
  const [selectedFile, setSelectedFile] = useState(null);

  // Category options
  const CATEGORY_CHOICES = [
    { value: 'GAMES', label: 'Games' },
    { value: 'PRODUCTIVITY', label: 'Productivity' },
    { value: 'SOCIAL', label: 'Social Media' },
    { value: 'UTILITY', label: 'Utility' },
    { value: 'OTHER', label: 'Other' }
  ];

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setFormData(prev => ({
      ...prev,
      app_icon: file
    }));
  };

  // Submit form for adding new app
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const appData = new FormData();
    Object.keys(formData).forEach(key => {
      // Only append non-null values
      if (formData[key] !== null && formData[key] !== '') {
        appData.append(key, formData[key]);
      }
    });

    try {
      // Create new app
      await api.post('apps/upload/', appData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      toast.success('App added successfully');
      navigate('/dashboard');
    } catch (error) {
      console.error('Error submitting app:', error);
      
      // More detailed error handling
      if (error.response) {
        toast.error(error.response.data.message || 'Submission failed');
      } else if (error.request) {
        toast.error('No response received from server');
      } else {
        toast.error('Error: ' + error.message);
      }
    }
  };

  // Cancel and return to dashboard
  const handleCancel = () => {
    navigate('/dashboard');
  };

  return (
    <div className="min-h-screen bg-gray-900 flex items-center justify-center">
      <div className="w-full max-w-2xl bg-black p-8 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-6 text-gray-200 text-center">
          Add New App
        </h2>
        
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="name" className="block font-medium text-gray-300">
              Name
            </label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
            />
          </div>

          <div>
            <label htmlFor="description" className="block font-medium text-gray-300">
              Description
            </label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
              rows="3"
            />
          </div>

          <div>
            <label htmlFor="package_name" className="block font-medium text-gray-300">
              Package Name
            </label>
            <input
              type="text"
              id="package_name"
              name="package_name"
              value={formData.package_name}
              onChange={handleChange}
              required
              className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
            />
          </div>

          <div>
            <label htmlFor="points_value" className="block font-medium text-gray-300">
              Points
            </label>
            <input
              type="number"
              id="points_value"
              name="points_value"
              value={formData.points_value}
              onChange={handleChange}
              min="1"
              className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
            />
          </div>

          <div>
            <label htmlFor="category" className="block font-medium text-gray-300">
              Category
            </label>
            <select
              id="category"
              name="category"
              value={formData.category}
              onChange={handleChange}
              className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
            >
              {CATEGORY_CHOICES.map((cat) => (
                <option key={cat.value} value={cat.value}>
                  {cat.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label htmlFor="app_icon" className="block font-medium text-gray-300">
              App Icon
            </label>
            <input
              type="file"
              id="app_icon"
              name="app_icon"
              onChange={handleFileChange}
              accept="image/*"
              className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
            />
            {selectedFile && (
              <p className="text-sm text-gray-400 mt-2">
                Selected: {selectedFile.name}
              </p>
            )}
          </div>

          <div>
            <label htmlFor="playstore_link" className="block font-medium text-gray-300">
              Play Store Link
            </label>
            <input
              type="url"
              id="playstore_link"
              name="playstore_link"
              value={formData.playstore_link}
              onChange={handleChange}
              className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
              placeholder="https://play.google.com/store/apps/details?id=..."
            />
          </div>

          <div className="flex items-center space-x-4">
            <label className="text-gray-300">
              <input
                type="checkbox"
                name="is_active"
                checked={formData.is_active}
                onChange={(e) => setFormData(prev => ({
                  ...prev, 
                  is_active: e.target.checked
                }))}
                className="mr-2"
              />
              Active App
            </label>
          </div>

          <div className="flex space-x-4 justify-center">
            <button
              type="submit"
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
            >
              Add App
            </button>
            <button
              type="button"
              onClick={handleCancel}
              className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddApp;
