import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { toast } from 'sonner';
import api from '../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const firstName = useSelector((state) => state.auth.first_name);
  
  // State for apps and form
  const [apps, setApps] = useState([]);
  const [currentApp, setCurrentApp] = useState({
    name: '',
    description: '',
    package_name: '',
    points_value: 10,
    category: 'OTHER',
    app_icon: null,
    playstore_link: '',
    is_active: true
  });
  const [isEditing, setIsEditing] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  // Category options
  const CATEGORY_CHOICES = [
    { value: 'GAMES', label: 'Games' },
    { value: 'PRODUCTIVITY', label: 'Productivity' },
    { value: 'SOCIAL', label: 'Social Media' },
    { value: 'UTILITY', label: 'Utility' },
    { value: 'OTHER', label: 'Other' }
  ];

  // Fetch apps on component mount
  useEffect(() => {
    fetchApps();
  }, []);

  // Fetch apps from backend
  const fetchApps = async () => {
    try {
      const response = await api.get('api/android-apps/');
      setApps(response.data);
    } catch (error) {
      console.error('Error fetching apps:', error);
      toast.error('Failed to fetch apps');
    }
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentApp(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setCurrentApp(prev => ({
      ...prev,
      app_icon: file
    }));
  };

  // Submit form for add/edit
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formData = new FormData();
    Object.keys(currentApp).forEach(key => {
      // Only append non-null values
      if (currentApp[key] !== null && currentApp[key] !== '') {
        formData.append(key, currentApp[key]);
      }
    });

    try {
      if (isEditing) {
        // Update existing app
        await api.put(`api/android-apps/${currentApp.id}/`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App updated successfully');
      } else {
        // Create new app
        await api.post('apps/upload/', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App added successfully');
      }
      
      // Reset form and refresh apps
      resetForm();
      fetchApps();
    } catch (error) {
      console.error('Error submitting app:', error);
      
      // More detailed error handling
      if (error.response) {
        // The request was made and the server responded with a status code
        toast.error(error.response.data.message || 'Submission failed');
      } else if (error.request) {
        // The request was made but no response was received
        toast.error('No response received from server');
      } else {
        // Something happened in setting up the request
        toast.error('Error: ' + error.message);
      }
    }
  };

  // Prepare app for editing
  const handleEdit = (app) => {
    setCurrentApp(app);
    setIsEditing(true);
  };

  // Delete app
  const handleDelete = async (appId) => {
    if (window.confirm('Are you sure you want to delete this app?')) {
      try {
        await api.delete(`api/android-apps/${appId}/`);
        toast.success('App deleted successfully');
        fetchApps();
      } catch (error) {
        console.error('Error deleting app:', error);
        toast.error('Failed to delete app');
      }
    }
  };

  // Reset form
  const resetForm = () => {
    setCurrentApp({
      name: '',
      description: '',
      package_name: '',
      points_value: 10,
      category: 'OTHER',
      app_icon: null,
      playstore_link: '',
      is_active: true
    });
    setSelectedFile(null);
    setIsEditing(false);
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-gray-100">
      {/* Navigation */}
      <nav className="bg-black/50 p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-white font-bold text-2xl">
            Android App Management Portal
          </h1>
          <div className="flex items-center space-x-4">
            <span className="text-gray-300 text-lg">Welcome, {firstName}</span>
            <button
              onClick={handleLogout}
              className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded transition duration-300"
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="container mx-auto p-6 grid md:grid-cols-2 gap-8 mt-6">
        {/* App Form */}
        <div className="bg-gray-800 p-6 rounded-lg shadow-xl">
          <h2 className="text-2xl font-bold mb-6 text-white">
            {isEditing ? 'Edit App' : 'Add New App'}
          </h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-gray-300 mb-2">App Name</label>
              <input
                type="text"
                name="name"
                value={currentApp.name}
                onChange={handleInputChange}
                required
                className="w-full bg-gray-700 text-white rounded px-3 py-2"
                placeholder="Enter app name"
              />
            </div>

            <div>
              <label className="block text-gray-300 mb-2">Description</label>
              <textarea
                name="description"
                value={currentApp.description}
                onChange={handleInputChange}
                className="w-full bg-gray-700 text-white rounded px-3 py-2"
                placeholder="Enter app description"
                rows="3"
              />
            </div>

            <div>
              <label className="block text-gray-300 mb-2">Package Name</label>
              <input
                type="text"
                name="package_name"
                value={currentApp.package_name}
                onChange={handleInputChange}
                required
                className="w-full bg-gray-700 text-white rounded px-3 py-2"
                placeholder="com.example.app"
              />
            </div>

            <div>
              <label className="block text-gray-300 mb-2">Points Value</label>
              <input
                type="number"
                name="points_value"
                value={currentApp.points_value}
                onChange={handleInputChange}
                min="1"
                className="w-full bg-gray-700 text-white rounded px-3 py-2"
              />
            </div>

            <div>
              <label className="block text-gray-300 mb-2">Category</label>
              <select
                name="category"
                value={currentApp.category}
                onChange={handleInputChange}
                className="w-full bg-gray-700 text-white rounded px-3 py-2"
              >
                {CATEGORY_CHOICES.map((cat) => (
                  <option key={cat.value} value={cat.value}>
                    {cat.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-gray-300 mb-2">App Icon</label>
              <input
                type="file"
                name="app_icon"
                onChange={handleFileChange}
                accept="image/*"
                className="w-full bg-gray-700 text-white rounded px-3 py-2"
              />
              {selectedFile && (
                <p className="text-sm text-gray-400 mt-2">
                  Selected: {selectedFile.name}
                </p>
              )}
            </div>

            <div>
              <label className="block text-gray-300 mb-2">Play Store Link</label>
              <input
                type="url"
                name="playstore_link"
                value={currentApp.playstore_link}
                onChange={handleInputChange}
                className="w-full bg-gray-700 text-white rounded px-3 py-2"
                placeholder="https://play.google.com/store/apps/details?id=..."
              />
            </div>

            <div className="flex items-center space-x-4">
              <label className="text-gray-300">
                <input
                  type="checkbox"
                  name="is_active"
                  checked={currentApp.is_active}
                  onChange={(e) => setCurrentApp(prev => ({
                    ...prev, 
                    is_active: e.target.checked
                  }))}
                  className="mr-2"
                />
                Active App
              </label>
            </div>

            <div className="flex space-x-4">
              <button
                type="submit"
                className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded transition duration-300"
              >
                {isEditing ? 'Update App' : 'Add App'}
              </button>
              {isEditing && (
                <button
                  type="button"
                  onClick={resetForm}
                  className="bg-gray-600 hover:bg-gray-700 text-white px-6 py-2 rounded transition duration-300"
                >
                  Cancel
                </button>
              )}
            </div>
          </form>
        </div>

        {/* Apps List */}
        <div className="bg-gray-800 p-6 rounded-lg shadow-xl">
          <h2 className="text-2xl font-bold mb-6 text-white">Existing Apps</h2>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-700">
                <tr>
                  <th className="px-4 py-3 text-left">Name</th>
                  <th className="px-4 py-3 text-left">Category</th>
                  <th className="px-4 py-3 text-left">Points</th>
                  <th className="px-4 py-3 text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {apps.map((app) => (
                  <tr 
                    key={app.id} 
                    className="border-b border-gray-700 hover:bg-gray-700/50 transition"
                  >
                    <td className="px-4 py-3">{app.name}</td>
                    <td className="px-4 py-3">{app.category}</td>
                    <td className="px-4 py-3">{app.points_value}</td>
                    <td className="px-4 py-3 flex justify-center space-x-2">
                      <button
                        onClick={() => handleEdit(app)}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(app.id)}
                        className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {apps.length === 0 && (
              <p className="text-center text-gray-400 mt-4">
                No apps found. Add a new app to get started.
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
