import React, { useState, useEffect } from 'react';
import { toast } from 'sonner';
import api from '../../services/api';

const AddAppModal = ({ 
  isOpen, 
  onClose, 
  initialData = null, 
  onAppAdded 
}) => {
  // Category options
  const CATEGORY_CHOICES = [
    { value: 'GAMES', label: 'Games' },
    { value: 'PRODUCTIVITY', label: 'Productivity' },
    { value: 'SOCIAL', label: 'Social Media' },
    { value: 'UTILITY', label: 'Utility' },
    { value: 'OTHER', label: 'Other' }
  ];

  // Initial form state
  const getInitialFormData = () => ({
    name: '',
    description: '',
    package_name: '',
    points_value: 10,
    category: 'OTHER',
    app_icon: null,
    playstore_link: '',
    is_active: true
  });

  const [formData, setFormData] = useState(getInitialFormData());
  const [selectedFile, setSelectedFile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [existingIconUrl, setExistingIconUrl] = useState(null);

  // Reset form when modal opens or closes
  useEffect(() => {
    if (initialData) {
      setFormData(initialData);
      setIsEditing(true);
      // Store existing icon URL
      setExistingIconUrl(initialData.app_icon);
    } else {
      setFormData(getInitialFormData());
      setIsEditing(false);
      setExistingIconUrl(null);
    }
    // Reset selected file when modal opens/closes
    setSelectedFile(null);
  }, [initialData, isOpen]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setFormData(prev => ({
      ...prev,
      app_icon: file
    }));
  };

  // Submit form for add/edit
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const appData = new FormData();
    
    // Append all form data
    Object.keys(formData).forEach(key => {
      if (formData[key] !== null && formData[key] !== '') {
        appData.append(key, formData[key]);
      }
    });

    // Special handling for app icon
    if (selectedFile) {
      // New file selected, append it
      appData.append('app_icon', selectedFile);
    } else if (isEditing && existingIconUrl) {
      // No new file, but editing and existing icon exists
      // Do not append app_icon to preserve existing icon
      delete appData.app_icon;
    }

    try {
      if (isEditing) {
        await api.put(`apps/${formData.id}/update/`, appData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App updated successfully');
      } else {
        await api.post('apps/upload/', appData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App added successfully');
      }
      
      // Close modal and refresh apps
      onAppAdded();
      onClose();
    } catch (error) {
      console.error('Error submitting app:', error);
      
      if (error.response) {
        toast.error(error.response.data.message || 'Submission failed');
      } else if (error.request) {
        toast.error('No response received from server');
      } else {
        toast.error('Error: ' + error.message);
      }
    }
  };

  // If modal is not open, return null
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-gray-800 rounded-lg p-6 w-full max-w-lg max-h-[90vh] overflow-y-auto">
        <h2 className="text-xl font-bold mb-4 text-gray-200">
          {isEditing ? 'Edit App' : 'Add New App'}
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Rest of the form remains the same */}
          <div>
            <label htmlFor="app_icon" className="block font-medium text-gray-300">
              App Icon
            </label>
            <input
              type="file"
              id="app_icon"
              name="app_icon"
              onChange={handleFileChange}
              accept="image/*"
              className="border rounded px-3 py-2 w-full bg-gray-700 text-gray-200"
            />
            {selectedFile && (
              <p className="text-sm text-gray-400 mt-2">
                Selected: {selectedFile.name}
              </p>
            )}
            {isEditing && existingIconUrl && !selectedFile && (
              <p className="text-sm text-gray-400 mt-2">
                Existing icon will be preserved
              </p>
            )}
          </div>

          {/* Rest of the form remains the same as before */}
          {/* ... other form fields ... */}
        </form>
      </div>
    </div>
  );
};

export default AddAppModal;
