import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { X } from "lucide-react";
import api from '../../services/api';

const Modal = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-gray-800 rounded-lg p-6 w-full max-w-md relative">
        <button
          onClick={onClose}
          className="absolute right-4 top-4 text-gray-400 hover:text-white"
        >
          <X size={24} />
        </button>
        <h2 className="text-xl font-bold text-white mb-4">{title}</h2>
        {children}
      </div>
    </div>
  );
};

const ClaimModal = ({ isOpen, onClose }) => {
  const [pendingTasks, setPendingTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);
  const [screenshot, setScreenshot] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchPendingTasks = async () => {
      try {
        const response = await api.get('/apps/available/');
        setPendingTasks(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch pending tasks');
        setLoading(false);
      }
    };

    if (isOpen) {
      fetchPendingTasks();
    }
  }, [isOpen]);

  const handleTaskSelect = (task) => {
    setSelectedTask(task);
    setScreenshot(null);
    setError(null);
  };

  const handleScreenshotChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        setError('Screenshot size should be less than 5MB');
        return;
      }
      setScreenshot(file);
      setError(null);
    }
  };

  const handleSubmit = async () => {
    if (!selectedTask || !screenshot) {
      setError('Please select a task and upload a screenshot');
      return;
    }

    setSubmitting(true);
    const formData = new FormData();
    formData.append('app', selectedTask.id);
    formData.append('screenshot', screenshot);

    try {
      await api.post('/tasks/submit/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setPendingTasks(pendingTasks.filter(task => task.id !== selectedTask.id));
      setSelectedTask(null);
      setScreenshot(null);
      setError('Task submitted successfully!');
      setTimeout(() => setError(null), 3000);
    } catch (err) {
      setError('Failed to submit task. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  if (!isOpen) return null;

  return (
    <Modal isOpen={isOpen} onClose={onClose} title="Claim Your Points">
      <div className="text-white">
        {error && (
          <div className={`p-3 rounded mb-4 ${
            error.includes('success') ? 'bg-green-500' : 'bg-red-500'
          }`}>
            {error}
          </div>
        )}

        {loading ? (
          <div className="text-center py-4">Loading tasks...</div>
        ) : pendingTasks.length === 0 ? (
          <div className="text-gray-300 text-center py-4">
            No pending tasks available.
          </div>
        ) : (
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              {pendingTasks.map((task) => (
                <div
                  key={task.id}
                  className={`p-3 rounded-lg cursor-pointer transition ${
                    selectedTask?.id === task.id
                      ? 'bg-blue-600'
                      : 'bg-gray-700 hover:bg-gray-600'
                  }`}
                  onClick={() => handleTaskSelect(task)}
                >
                  {task.app_icon && (
                    <img
                      src={task.app_icon}
                      alt={task.name}
                      className="w-full h-20 object-contain mb-2"
                    />
                  )}
                  <p className="text-white text-center text-sm">{task.name}</p>
                  <p className="text-green-400 text-center text-sm">
                    {task.points_value} Points
                  </p>
                </div>
              ))}
            </div>

            {selectedTask && (
              <div className="space-y-3">
                <p className="text-white">Selected: {selectedTask.name}</p>
                <div className="space-y-2">
                  <label className="block text-gray-300">Upload Screenshot</label>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleScreenshotChange}
                    className="w-full text-gray-300"
                  />
                </div>

                <button
                  onClick={handleSubmit}
                  disabled={submitting || !screenshot}
                  className={`w-full py-2 rounded transition ${
                    submitting || !screenshot
                      ? 'bg-gray-600 cursor-not-allowed'
                      : 'bg-green-600 hover:bg-green-700'
                  } text-white`}
                >
                  {submitting ? 'Submitting...' : 'Submit Task'}
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    </Modal>
  );
};

const Dashboard = () => {
  const navigate = useNavigate();
  const [apps, setApps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isClaimModalOpen, setIsClaimModalOpen] = useState(false);
  const [isProfileModalOpen, setIsProfileModalOpen] = useState(false);
  const [userProfile, setUserProfile] = useState(null);

  const firstName = useSelector((state) => state.auth.first_name);
  const email = useSelector((state) => state.auth.email);
  
  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const handleDownload = (playstoreLink, appName) => {
    if (playstoreLink) {
      window.open(playstoreLink, '_blank');
    } else {
      setError(`Playstore link for ${appName} is not available`);
      setTimeout(() => setError(null), 3000);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [appsResponse] = await Promise.all([
          api.get('/apps/available/'),
        ]);
        setApps(appsResponse.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch data");
        setLoading(false);
        console.error("Error fetching data:", err);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="min-h-screen bg-gray-900">
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-gray-200 font-bold text-2xl">
            Android App Download Portal
          </h1>
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setIsClaimModalOpen(true)}
              className="bg-green-600 px-4 py-2 rounded text-white hover:bg-green-700 transition"
            >
              Claim Points
            </button>
            <button
              onClick={() => setIsProfileModalOpen(true)}
              className="bg-blue-600 px-4 py-2 rounded text-white hover:bg-blue-700 transition"
            >
              Profile
            </button>
            <h1 className="text-gray-300 text-lg">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-4 py-2 rounded text-gray-200 hover:bg-gray-700 transition"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      <div className="container mx-auto px-6 py-8">
        <h2 className="text-3xl font-bold text-white text-center mb-8 border-b border-gray-700 pb-4">
          Available Apps
        </h2>

        {error && (
          <div className="bg-red-500 text-white p-3 rounded mb-4 transition-opacity">
            {error}
          </div>
        )}

        {loading ? (
          <div className="text-white text-center">Loading...</div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {apps.map((app) => (
              <div 
                key={app.id} 
                className="bg-gray-800 rounded-lg overflow-hidden shadow-lg"
              >
                {app.app_icon && (
                  <img 
                    src={app.app_icon} 
                    alt={app.name} 
                    className="w-full h-32 object-contain"
                  />
                )}
                <div className="p-4">
                  <h3 className="text-xl font-semibold text-white mb-2">{app.name}</h3>
                  <p className="text-gray-400 mb-2">{app.description}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-green-500 font-bold">
                      {app.points_value} Points
                    </span>
                    <button
                      className={`px-3 py-1 rounded transition ${
                        app.playstore_link 
                          ? 'bg-blue-600 hover:bg-blue-700 text-white' 
                          : 'bg-gray-600 cursor-not-allowed text-gray-300'
                      }`}
                      onClick={() => handleDownload(app.playstore_link, app.name)}
                    >
                      Download
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <ClaimModal
        isOpen={isClaimModalOpen}
        onClose={() => setIsClaimModalOpen(false)}
      />

      <Modal
        isOpen={isProfileModalOpen}
        onClose={() => setIsProfileModalOpen(false)}
        title="Your Profile"
      >
        <div className="text-white">
          <div className="space-y-4">
            <div>
              <label className="block text-gray-400 mb-1">Name</label>
              <p className="text-lg">{firstName}</p>
            </div>
            <div>
              <label className="block text-gray-400 mb-1">Email</label>
              <p className="text-lg">{email}</p>
            </div>
            <div>
              <label className="block text-gray-400 mb-1">Total Points</label>
              <p className="text-lg text-green-500 font-bold">
                {userProfile?.total_points || 0}
              </p>
            </div>
            <button
              onClick={() => setIsProfileModalOpen(false)}
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            >
              Close
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default Dashboard;
