import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import api from '../../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const [apps, setApps] = useState([]);
  const [completedApps, setCompletedApps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const firstName = useSelector((state) => state.auth.first_name);
  const userId = useSelector((state) => state.auth.user_id);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch all apps
        const appsResponse = await api.get('/apps/');
        
        // Fetch user's completed app tasks
        const completedAppsResponse = await api.get(`/user-app-tasks/?user=${userId}&status=APPROVED`);
        
        // Get the package names of completed apps
        const completedAppPackages = completedAppsResponse.data.map(task => task.app.package_name);
        
        // Filter out apps that are already completed
        const availableApps = appsResponse.data.filter(
          app => !completedAppPackages.includes(app.package_name)
        );

        setApps(availableApps);
        setCompletedApps(completedAppPackages);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch apps");
        setLoading(false);
        console.error("Error fetching apps:", err);
      }
    };

    fetchData();
  }, [userId]);

  return (
    <div className="min-h-screen bg-gray-900">
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-gray-200 font-bold text-2xl">
            Android App Download Portal
          </h1>
          <div className="flex items-center space-x-4 ml-auto">
            <h1 className="text-gray-300 text-lg">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-4 py-2 rounded text-gray-200 hover:bg-gray-700 transition duration-300"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>
      <div className="container mx-auto flex flex-row mt-6 justify-between px-6 gap-6">
        <div className="w-full">
          <h2 className="text-2xl font-bold mb-4 text-white">Available Apps</h2>
          {loading ? (
            <div className="text-white">Loading...</div>
          ) : error ? (
            <div className="text-red-500">{error}</div>
          ) : apps.length === 0 ? (
            <div className="text-white text-center">No new apps available</div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {apps.map((app) => (
                <div
                  key={app.id}
                  className="bg-gray-800 rounded-lg overflow-hidden shadow-lg"
                >
                  {app.app_icon && (
                    <img
                      src={app.app_icon}
                      alt={app.name}
                      className="w-full h-32 object-contain"
                    />
                  )}
                  <div className="p-4">
                    <h3 className="text-xl font-semibold text-white mb-2">{app.name}</h3>
                    <p className="text-gray-400 mb-2 line-clamp-2">{app.description}</p>
                    <div className="flex justify-between items-center">
                      <span className="text-green-500 font-bold">
                        {app.points_value} Points
                      </span>
                      <button
                        className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 transition"
                      >
                        Download
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
