import React, { useState, useEffect } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Download } from "lucide-react";

const ManagerLeaveReport = () => {
  const [report, setReport] = useState([]);
  const baseURL = "http://127.0.0.1:8000";

  useEffect(() => {
    const fetchReport = async () => {
      try {
        const token = localStorage.getItem("access");
        const response = await axios.get(baseURL + "/leave/manager/report/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setReport(response.data.report);
      } catch (error) {
        console.error("Error fetching report:", error);
      }
    };

    fetchReport();
  }, []);

  const downloadPDF = () => {
    const doc = new jsPDF();

    // Add title
    doc.setFontSize(16);
    doc.text("Employee Leave Report", 14, 20);
    doc.setFontSize(12);
    doc.text(`Generated on: ${new Date().toLocaleDateString()}`, 14, 30);

    // Check if there is data to display
    if (report.length > 0) {
      // Define table headers
      const headers = [
        ["Employee", "Leave Type", "Date", "Reason", "Status"]
      ];

      // Define table rows
      const data = report.map((leave) => [
        leave.employee,
        leave.leave_type.charAt(0).toUpperCase() + leave.leave_type.slice(1),
        new Date(leave.date).toLocaleDateString(),
        leave.reason,
        leave.status.charAt(0).toUpperCase() + leave.status.slice(1)
      ]);

      // Add table to PDF using autoTable
      doc.autoTable({
        head: headers,
        body: data,
        startY: 40,
        styles: {
          fontSize: 10,
          cellPadding: 3
        },
        headStyles: {
          fillColor: [41, 128, 185],
          textColor: 255
        },
        alternateRowStyles: {
          fillColor: [245, 245, 245]
        }
      });

      // Save the PDF
      doc.save("employee-leave-report.pdf");
    } else {
      // If no leave history found, show a message in the PDF
      doc.text("No leave history available.", 14, 40);
      doc.save("employee-leave-report.pdf");
    }
  };

  return (
    <Card className="w-full">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle>All Employees Leave Report</CardTitle>
        <Button 
          onClick={downloadPDF}
          className="bg-green-600 hover:bg-green-700"
        >
          <Download className="mr-2 h-4 w-4" />
          Download PDF
        </Button>
      </CardHeader>
      <CardContent>
        {report.length ? (
          <div className="overflow-x-auto">
            <table className="w-full border-collapse">
              <thead>
                <tr className="bg-gray-100">
                  <th className="p-3 text-left border">Employee</th>
                  <th className="p-3 text-left border">Leave Type</th>
                  <th className="p-3 text-left border">Date</th>
                  <th className="p-3 text-left border">Reason</th>
                  <th className="p-3 text-left border">Status</th>
                </tr>
              </thead>
              <tbody>
                {report.map((leave, index) => (
                  <tr key={index} className="hover:bg-gray-50">
                    <td className="p-3 border">{leave.employee}</td>
                    <td className="p-3 border">
                      {leave.leave_type.charAt(0).toUpperCase() + 
                       leave.leave_type.slice(1)}
                    </td>
                    <td className="p-3 border">
                      {new Date(leave.date).toLocaleDateString()}
                    </td>
                    <td className="p-3 border">{leave.reason}</td>
                    <td className="p-3 border">
                      {leave.status.charAt(0).toUpperCase() + 
                       leave.status.slice(1)}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p className="text-center py-4">No leave data available</p>
        )}
      </CardContent>
    </Card>
  );
};

export default ManagerLeaveReport;
