import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { toast } from 'sonner';
import api from '../../services/api';

// Modal Component
const Modal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto outline-none focus:outline-none">
      <div className="relative w-auto max-w-3xl mx-auto my-6">
        <div className="relative flex flex-col w-full bg-gray-800 border-0 rounded-lg shadow-lg outline-none focus:outline-none">
          <div className="relative p-6 flex-auto">
            {children}
          </div>
          <div className="flex items-center justify-end p-6 border-t border-solid rounded-b border-gray-700">
            <button
              className="px-6 py-2 mb-1 mr-1 text-sm font-bold text-white uppercase transition-all duration-150 ease-linear bg-gray-600 rounded shadow outline-none hover:bg-gray-700 hover:shadow-lg focus:outline-none"
              type="button"
              onClick={onClose}
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

const Dashboard = () => {
  const navigate = useNavigate();
  const firstName = useSelector((state) => state.auth.first_name);
  
  // State for apps and form
  const [apps, setApps] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    package_name: '',
    points_value: 10,
    category: 'OTHER',
    app_icon: null,
    playstore_link: '',
    is_active: true
  });
  const [isEditing, setIsEditing] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  
  // Modal states
  const [isAddAppModalOpen, setIsAddAppModalOpen] = useState(false);
  const [isPendingApprovalModalOpen, setIsPendingApprovalModalOpen] = useState(false);

  // Category options
  const CATEGORY_CHOICES = [
    { value: 'GAMES', label: 'Games' },
    { value: 'PRODUCTIVITY', label: 'Productivity' },
    { value: 'SOCIAL', label: 'Social Media' },
    { value: 'UTILITY', label: 'Utility' },
    { value: 'OTHER', label: 'Other' }
  ];

  // Fetch apps on component mount
  useEffect(() => {
    fetchApps();
  }, []);

  // Fetch apps from backend
  const fetchApps = async () => {
    try {
      const response = await api.get('apps/');
      setApps(response.data);
    } catch (error) {
      console.error('Error fetching apps:', error);
      toast.error('Failed to fetch apps');
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setFormData(prev => ({
      ...prev,
      app_icon: file
    }));
  };

  // Submit form for add/edit
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const appData = new FormData();
    Object.keys(formData).forEach(key => {
      if (formData[key] !== null && formData[key] !== '') {
        if (key === 'app_icon') {
          if (selectedFile) {
            appData.append(key, formData[key]);
          }
        } else {
          appData.append(key, formData[key]);
        }
      }
    });

    try {
      if (isEditing) {
        await api.put(`apps/${formData.id}/update/`, appData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App updated successfully');
      } else {
        await api.post('apps/upload/', appData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App added successfully');
      }
      
      resetForm();
      fetchApps();
      setIsAddAppModalOpen(false);
    } catch (error) {
      console.error('Error submitting app:', error);
      
      if (error.response) {
        toast.error(error.response.data.message || 'Submission failed');
      } else if (error.request) {
        toast.error('No response received from server');
      } else {
        toast.error('Error: ' + error.message);
      }
    }
  };

  // Prepare app for editing
  const handleEdit = (app) => {
    setFormData(app);
    setIsEditing(true);
    setIsAddAppModalOpen(true);
  };

  // Delete app
  const handleDelete = async (appId) => {
    if (window.confirm('Are you sure you want to delete this app?')) {
      try {
        await api.delete(`apps/${appId}/delete/`);
        toast.success('App deleted successfully');
        fetchApps();
      } catch (error) {
        console.error('Error deleting app:', error);
        toast.error('Failed to delete app');
      }
    }
  };

  // Reset form
  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      package_name: '',
      points_value: 10,
      category: 'OTHER',
      app_icon: null,
      playstore_link: '',
      is_active: true
    });
    setSelectedFile(null);
    setIsEditing(false);
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => {
                resetForm();
                setIsAddAppModalOpen(true);
              }}
              className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded transition duration-300"
            >
              Add New App
            </button>
            <button
              onClick={() => setIsPendingApprovalModalOpen(true)}
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300"
            >
              Pending Approval
            </button>
          </div>
          <div className="flex items-center space-x-4 ml-auto">
            <h1 className="text-gray-300 text-lg">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-4 py-2 rounded text-gray-200 hover:bg-gray-700 transition duration-300"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      <div className="container mx-auto flex flex-row mt-6 justify-between px-6 gap-6">
        {/* App Form */}
        <div>
          <h2 className="text-xl font-bold mb-2 text-gray-200">
            {isEditing ? 'Edit App' : 'Add New App'}
          </h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="name" className="block font-medium text-gray-300">
                Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
                className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
              />
            </div>

            <div>
              <label htmlFor="description" className="block font-medium text-gray-300">
                Description
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
                rows="3"
              />
            </div>

            <div>
              <label htmlFor="package_name" className="block font-medium text-gray-300">
                Package Name
              </label>
              <input
                type="text"
                id="package_name"
                name="package_name"
                value={formData.package_name}
                onChange={handleChange}
                required
                className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
              />
            </div>

            <div>
              <label htmlFor="points_value" className="block font-medium text-gray-300">
                Points
              </label>
              <input
                type="number"
                id="points_value"
                name="points_value"
                value={formData.points_value}
                onChange={handleChange}
                min="1"
                className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
              />
            </div>

            <div>
              <label htmlFor="category" className="block font-medium text-gray-300">
                Category
              </label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
              >
                {CATEGORY_CHOICES.map((cat) => (
                  <option key={cat.value} value={cat.value}>
                    {cat.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label htmlFor="app_icon" className="block font-medium text-gray-300">
                App Icon
              </label>
              <input
                type="file"
                id="app_icon"
                name="app_icon"
                onChange={handleFileChange}
                accept="image/*"
                className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
              />
              {selectedFile && (
                <p className="text-sm text-gray-400 mt-2">
                  Selected: {selectedFile.name}
                </p>
              )}
            </div>

            <div>
              <label htmlFor="playstore_link" className="block font-medium text-gray-300">
                Play Store Link
              </label>
              <input
                type="url"
                id="playstore_link"
                name="playstore_link"
                value={formData.playstore_link}
                onChange={handleChange}
                className="border rounded px-3 py-2 w-full bg-gray-800 text-gray-200"
                placeholder="https://play.google.com/store/apps/details?id=..."
              />
            </div>

            <div className="flex items-center space-x-4">
              <label className="text-gray-300">
                <input
                  type="checkbox"
                  name="is_active"
                  checked={formData.is_active}
                  onChange={(e) => setFormData(prev => ({
                    ...prev, 
                    is_active: e.target.checked
                  }))}
                  className="mr-2"
                />
                Active App
              </label>
            </div>

            <div className="flex space-x-4">
              <button
                type="submit"
                className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
              >
                {isEditing ? 'Update App' : 'Add App'}
              </button>
              {isEditing && (
                <button
                  type="button"
                  onClick={resetForm}
                  className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
                >
                  Cancel
                </button>
              )}
            </div>
          </form>
        </div>

        {/* Apps List */}
        <div>
          <h2 className="text-xl font-bold mb-2 text-gray-200">Existing Apps</h2>
          <table className="w-full">
            <thead>
              <tr className="bg-gray-800 text-gray-300">
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Points</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {apps.map((app) => (
                <tr key={app.id} className="border-b border-gray-800">
                  <td className="border px-4 py-2 text-gray-200">{app.name}</td>
                  <td className="border px-4 py-2 text-gray-200">{app.category}</td>
                  <td className="border px-4 py-2 text-gray-200">{app.points_value}</td>
                  <td className="border px-4 py-2">
                    <button
                      onClick={() => handleEdit(app)}
                      className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(app.id)}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modals */}
      <Modal 
        isOpen={isAddAppModalOpen} 
        onClose={() => {
          setIsAddAppModalOpen(false);
          resetForm();
        }}
      >
        <div>
          <h2 className="text-2xl font-bold mb-4 text-gray-200">
            {isEditing ? 'Edit App' : 'Add New App'}
          </h2>
          {/* Modal content is similar to the main form, can be extracted to a separate component if needed */}
        </div>
      </Modal>

      <Modal 
        isOpen={isPendingApprovalModalOpen} 
        onClose={() => setIsPendingApprovalModalOpen(false)}
      >
       <div>
          <h2 className="text-2xl font-bold mb-4 text-gray-200">
            Pending Approval Apps
          </h2>
          <p className="text-gray-400">No pending apps at the moment.</p>
        </div>
      </Modal>
    </div>
  );
};

export default Dashboard;
