import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import api from '../../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const [apps, setApps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const firstName = useSelector((state) => state.auth.first_name);
 
  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const handleDownload = (playstoreLink, appName) => {
    if (playstoreLink) {
      // Open playstore link in a new tab
      window.open(playstoreLink, '_blank');
    } else {
      // Show error message if playstore link is not available
      setError(`Playstore link for ${appName} is not available`);
      // Clear error after 3 seconds
      setTimeout(() => setError(null), 3000);
    }
  };

  useEffect(() => {
    const fetchApps = async () => {
      try {
        const response = await api.get('/apps/available/');
        setApps(response.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch apps");
        setLoading(false);
        console.error("Error fetching apps:", err);
      }
    };

    fetchApps();
  }, []);

  return (
    <div className="min-h-screen bg-gray-900">
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-gray-200 font-bold text-2xl">
            Android App download Portal
          </h1>
          <div className="flex items-center space-x-4 ml-auto">
            <h1 className="text-gray-300 text-lg">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-4 py-2 rounded text-gray-200 hover:bg-gray-700 transition duration-300"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>
      <div className="container mx-auto flex flex-row mt-6 justify-between px-6 gap-6">
        <div className="w-full">
          <h2 className="text-2xl font-bold mb-4 text-white">Available Apps</h2>
          {error && (
            <div className="bg-red-500 text-white p-3 rounded mb-4 transition-opacity">
              {error}
            </div>
          )}
          {loading ? (
            <div className="text-white">Loading...</div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {apps.map((app) => (
                <div 
                  key={app.id} 
                  className="bg-gray-800 rounded-lg overflow-hidden shadow-lg"
                >
                  {app.app_icon && (
                    <img 
                      src={app.app_icon} 
                      alt={app.name} 
                      className="w-full h-32 object-contain"
                    />
                  )}
                  <div className="p-4">
                    <h3 className="text-xl font-semibold text-white mb-2">{app.name}</h3>
                    <p className="text-gray-400 mb-2">{app.description}</p>
                    <div className="flex justify-between items-center">
                      <span className="text-green-500 font-bold">
                        {app.points_value} Points
                      </span>
                      <button
                        className={`px-3 py-1 rounded transition ${
                          app.playstore_link 
                            ? 'bg-blue-600 hover:bg-blue-700 text-white' 
                            : 'bg-gray-600 cursor-not-allowed text-gray-300'
                        }`}
                        onClick={() => handleDownload(app.playstore_link, app.name)}
                      >
                        Download
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
