import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { toast } from 'sonner';
import { 
  PlusIcon, 
  ClockIcon, 
  EditIcon, 
  TrashIcon, 
  XIcon 
} from 'lucide-react';
import api from '../../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const firstName = useSelector((state) => state.auth.first_name);
  
  // State for modals and data
  const [apps, setApps] = useState([]);
  const [pendingApps, setPendingApps] = useState([]);
  const [isAddAppModalOpen, setIsAddAppModalOpen] = useState(false);
  const [isPendingAppsModalOpen, setIsPendingAppsModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    package_name: '',
    points_value: 10,
    category: 'OTHER',
    app_icon: null,
    playstore_link: '',
    is_active: true
  });
  const [isEditing, setIsEditing] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  // Category options
  const CATEGORY_CHOICES = [
    { value: 'GAMES', label: 'Games' },
    { value: 'PRODUCTIVITY', label: 'Productivity' },
    { value: 'SOCIAL', label: 'Social Media' },
    { value: 'UTILITY', label: 'Utility' },
    { value: 'OTHER', label: 'Other' }
  ];

  // Fetch apps and pending apps on component mount
  useEffect(() => {
    fetchApps();
    fetchPendingApps();
  }, []);

  // Fetch apps from backend
  const fetchApps = async () => {
    try {
      const response = await api.get('apps/');
      setApps(response.data);
    } catch (error) {
      console.error('Error fetching apps:', error);
      toast.error('Failed to fetch apps');
    }
  };

  // Fetch pending apps
  const fetchPendingApps = async () => {
    try {
      const response = await api.get('apps/pending/');
      setPendingApps(response.data);
    } catch (error) {
      console.error('Error fetching pending apps:', error);
      toast.error('Failed to fetch pending apps');
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setFormData(prev => ({
      ...prev,
      app_icon: file
    }));
  };

  // Submit form for add/edit
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const appData = new FormData();
    Object.keys(formData).forEach(key => {
      if (formData[key] !== null && formData[key] !== '') {
        if (key === 'app_icon') {
          if (selectedFile) {
            appData.append(key, formData[key]);
          }
        } else {
          appData.append(key, formData[key]);
        }
      }
    });

    try {
      if (isEditing) {
        await api.put(`apps/${formData.id}/update/`, appData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App updated successfully');
      } else {
        await api.post('apps/upload/', appData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        toast.success('App added successfully');
      }
      
      resetForm();
      fetchApps();
      fetchPendingApps();
      setIsAddAppModalOpen(false);
    } catch (error) {
      console.error('Error submitting app:', error);
      
      if (error.response) {
        toast.error(error.response.data.message || 'Submission failed');
      } else if (error.request) {
        toast.error('No response received from server');
      } else {
        toast.error('Error: ' + error.message);
      }
    }
  };

  // Prepare app for editing
  const handleEdit = (app) => {
    setFormData(app);
    setIsEditing(true);
    setIsAddAppModalOpen(true);
  };

  // Delete app
  const handleDelete = async (appId) => {
    if (window.confirm('Are you sure you want to delete this app?')) {
      try {
        await api.delete(`apps/${appId}/delete/`);
        toast.success('App deleted successfully');
        fetchApps();
        fetchPendingApps();
      } catch (error) {
        console.error('Error deleting app:', error);
        toast.error('Failed to delete app');
      }
    }
  };

  // Reset form
  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      package_name: '',
      points_value: 10,
      category: 'OTHER',
      app_icon: null,
      playstore_link: '',
      is_active: true
    });
    setSelectedFile(null);
    setIsEditing(false);
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  // Modal for Add/Edit App
  const AddAppModal = () => (
    <div 
      className={`fixed inset-0 z-50 flex items-center justify-center ${
        isAddAppModalOpen ? 'visible' : 'invisible'
      }`}
    >
      <div 
        className={`fixed inset-0 bg-black opacity-50 ${
          isAddAppModalOpen ? 'block' : 'hidden'
        }`}
        onClick={() => setIsAddAppModalOpen(false)}
      ></div>
      <div 
        className={`bg-gray-800 rounded-lg shadow-xl p-6 z-60 w-[500px] max-h-[90vh] overflow-y-auto transform transition-all duration-300 ${
          isAddAppModalOpen 
            ? 'opacity-100 scale-100' 
            : 'opacity-0 scale-90'
        }`}
      >
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold text-gray-200">
            {isEditing ? 'Edit App' : 'Add New App'}
          </h2>
          <button 
            onClick={() => {
              setIsAddAppModalOpen(false);
              resetForm();
            }}
            className="text-gray-400 hover:text-gray-200"
          >
            <XIcon />
          </button>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Form fields remain the same as in the previous code */}
          {/* (Copy the entire form from the previous code) */}
          {/* ... */}
        </form>
      </div>
    </div>
  );

  // Modal for Pending Apps
  const PendingAppsModal = () => (
    <div 
      className={`fixed inset-0 z-50 flex items-center justify-center ${
        isPendingAppsModalOpen ? 'visible' : 'invisible'
      }`}
    >
      <div 
        className={`fixed inset-0 bg-black opacity-50 ${
          isPendingAppsModalOpen ? 'block' : 'hidden'
        }`}
        onClick={() => setIsPendingAppsModalOpen(false)}
      ></div>
      <div 
        className={`bg-gray-800 rounded-lg shadow-xl p-6 z-60 w-[700px] max-h-[90vh] overflow-y-auto transform transition-all duration-300 ${
          isPendingAppsModalOpen 
            ? 'opacity-100 scale-100' 
            : 'opacity-0 scale-90'
        }`}
      >
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold text-gray-200">
            Pending Apps Approval
          </h2>
          <button 
            onClick={() => setIsPendingAppsModalOpen(false)}
            className="text-gray-400 hover:text-gray-200"
          >
            <XIcon />
          </button>
        </div>
        <table className="w-full">
          <thead>
            <tr className="bg-gray-700 text-gray-300">
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Category</th>
              <th className="px-4 py-2">Points</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {pendingApps.map((app) => (
              <tr key={app.id} className="border-b border-gray-700">
                <td className="border px-4 py-2 text-gray-200">{app.name}</td>
                <td className="border px-4 py-2 text-gray-200">{app.category}</td>
                <td className="border px-4 py-2 text-gray-200">{app.points_value}</td>
                <td className="border px-4 py-2 flex space-x-2">
                  <button
                    onClick={() => {/* Approve logic */}}
                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-2 rounded"
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => {/* Reject logic */}}
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded"
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-900">
      {/* Modals */}
      <AddAppModal />
      <PendingAppsModal />

      {/* Navigation */}
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <button 
              onClick={() => setIsAddAppModalOpen(true)}
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded flex items-center space-x-2"
            >
              <PlusIcon className="w-5 h-5" />
              <span>Add New App</span>
            </button>
            <button 
              onClick={() => setIsPendingAppsModalOpen(true)}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center space-x-2"
            >
              <ClockIcon className="w-5 h-5" />
              <span>Pending Approval</span>
            </button>
          </div>
          
          <div className="flex items-center space-x-4 ml-auto">
            <h1 className="text-gray-300 text-lg">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-4 py-2 rounded text-gray-200 hover:bg-gray-700 transition duration-300"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      {/* Apps List */}
      <div className="container mx-auto mt-6 px-6">
        <h2 className="text-xl font-bold mb-4 text-gray-200">Existing Apps</h2>
        <table className="w-full">
          <thead>
            <tr className="bg-gray-800 text-gray-300">
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Category</th>
              <th className="px-4 py-2">Points</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {apps.map((app) => (
              <tr key={app.id} className="border-b border-gray-800">
                <td className="border px-4 py-2 text-gray-200">{app.name}</td>
                <td className="border px-4 py-2 text-gray-200">{app.category}</td>
                <td className="border px-4 py-2 text-gray-200">{app.points_value}</td>
                <td className="border px-4 py-2 flex space-x-2">
                  <button
                    onClick={() => handleEdit(app)}
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center space-x-2"
                  >
                    <EditIcon className="w-4 h-4" />
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(app.id)}
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded flex items-center space-x-2"
                  >
                    <TrashIcon className="w-4 h-4" />
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
