import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { toast } from 'sonner';
import { 
  PlusIcon, 
  ClockIcon, 
  EditIcon, 
  TrashIcon, 
  XIcon 
} from 'lucide-react';
import api from '../../services/api';
import AddAppModal from './AddAppModal';
import PendingAppsModal from './PendingAppsModal';

const Dashboard = () => {
  const navigate = useNavigate();
  const firstName = useSelector((state) => state.auth.first_name);
  
  // State for apps and modals
  const [apps, setApps] = useState([]);
  const [pendingApps, setPendingApps] = useState([]);
  const [isAddAppModalOpen, setIsAddAppModalOpen] = useState(false);
  const [isPendingAppsModalOpen, setIsPendingAppsModalOpen] = useState(false);
  const [currentApp, setCurrentApp] = useState(null);

  // Fetch apps and pending apps on component mount
  useEffect(() => {
    fetchApps();
    fetchPendingApps();
  }, []);

  // Fetch approved apps
  const fetchApps = async () => {
    try {
      const response = await api.get('/apps/');
      setApps(response.data);
    } catch (error) {
      console.error('Error fetching apps:', error);
      toast.error('Failed to fetch apps');
    }
  };

  // Fetch pending apps
  const fetchPendingApps = async () => {
    try {
      const response = await api.get('/apps/pending/');
      setPendingApps(response.data);
    } catch (error) {
      console.error('Error fetching pending apps:', error);
      toast.error('Failed to fetch pending apps');
    }
  };

  // Handle app edit
  const handleEdit = (app) => {
    setCurrentApp(app);
    setIsAddAppModalOpen(true);
  };

  // Handle app delete
  const handleDelete = async (appId) => {
    if (window.confirm('Are you sure you want to delete this app?')) {
      try {
        await api.delete(`/apps/${appId}/`);
        toast.success('App deleted successfully');
        fetchApps();
      } catch (error) {
        console.error('Error deleting app:', error);
        toast.error('Failed to delete app');
      }
    }
  };

  // Handle app approval
  const handleAppApproval = async (appId, status) => {
    try {
      await api.patch(`/apps/${appId}/approve/`, { status });
      toast.success(`App ${status === 'approved' ? 'approved' : 'rejected'} successfully`);
      fetchPendingApps();
      fetchApps();
    } catch (error) {
      console.error('Error updating app status:', error);
      toast.error('Failed to update app status');
    }
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-900 text-gray-200">
      {/* Modals */}
      <AddAppModal 
        isOpen={isAddAppModalOpen} 
        onClose={() => {
          setIsAddAppModalOpen(false);
          setCurrentApp(null);
        }}
        app={currentApp}
        onAppAdded={fetchApps}
      />
      <PendingAppsModal 
        isOpen={isPendingAppsModalOpen} 
        onClose={() => setIsPendingAppsModalOpen(false)}
        pendingApps={pendingApps}
        onApprove={(appId) => handleAppApproval(appId, 'approved')}
        onReject={(appId) => handleAppApproval(appId, 'rejected')}
      />

      {/* Navigation */}
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <button 
              onClick={() => setIsAddAppModalOpen(true)}
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded flex items-center space-x-2"
            >
              <PlusIcon className="w-5 h-5" />
              <span>Add New App</span>
            </button>
            <button 
              onClick={() => setIsPendingAppsModalOpen(true)}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center space-x-2"
            >
              <ClockIcon className="w-5 h-5" />
              <span>Pending Approval ({pendingApps.length})</span>
            </button>
          </div>
          
          <div className="flex items-center space-x-4 ml-auto">
            <h1 className="text-gray-300 text-lg">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-4 py-2 rounded text-gray-200 hover:bg-gray-700 transition duration-300"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      {/* Apps List */}
      <div className="container mx-auto mt-6 px-6">
        <h2 className="text-xl font-bold mb-4 text-gray-200">Existing Apps</h2>
        <table className="w-full">
          <thead>
            <tr className="bg-gray-800 text-gray-300">
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Category</th>
              <th className="px-4 py-2">Points</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {apps.map((app) => (
              <tr key={app.id} className="border-b border-gray-800">
                <td className="border px-4 py-2 text-gray-200">{app.name}</td>
                <td className="border px-4 py-2 text-gray-200">{app.category}</td>
                <td className="border px-4 py-2 text-gray-200">{app.points_value}</td>
                <td className="border px-4 py-2">
                  <span className={`
                    px-2 py-1 rounded text-xs 
                    ${app.is_active ? 'bg-green-500' : 'bg-red-500'}
                  `}>
                    {app.is_active ? 'Active' : 'Inactive'}
                  </span>
                </td>
                <td className="border px-4 py-2 flex space-x-2">
                  <button
                    onClick={() => handleEdit(app)}
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center space-x-2"
                  >
                    <EditIcon className="w-4 h-4" />
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(app.id)}
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded flex items-center space-x-2"
                  >
                    <TrashIcon className="w-4 h-4" />
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
