import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import api from '../../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const [apps, setApps] = useState([]);
  const [userAppTasks, setUserAppTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const firstName = useSelector((state) => state.auth.first_name);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch available apps
        const appsResponse = await api.get('/apps/');
        
        // Fetch user's app tasks to determine status
        const userAppTasksResponse = await api.get('/user-app-tasks/');
        
        setApps(appsResponse.data);
        setUserAppTasks(userAppTasksResponse.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch apps");
        setLoading(false);
        console.error("Error fetching apps:", err);
      }
    };

    fetchData();
  }, []);

  const getAppStatus = (appId) => {
    const task = userAppTasks.find(task => task.app === appId);
    return task ? task.status : 'NOT_DOWNLOADED';
  };

  const handleDownload = async (app) => {
    try {
      // Create a new app task for the user
      await api.post('/user-app-tasks/', {
        app: app.id,
        status: 'PENDING'
      });

      // Redirect to playstore link if available
      if (app.playstore_link) {
        window.open(app.playstore_link, '_blank');
      }

      // Refresh user app tasks
      const userAppTasksResponse = await api.get('/user-app-tasks/');
      setUserAppTasks(userAppTasksResponse.data);
    } catch (err) {
      console.error("Error creating app task:", err);
    }
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'APPROVED': return 'text-green-500';
      case 'REJECTED': return 'text-red-500';
      case 'SUBMITTED': return 'text-yellow-500';
      case 'PENDING': return 'text-blue-500';
      default: return 'text-gray-500';
    }
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <nav className="bg-black p-4 shadow-lg border-b border-gray-800">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-gray-200 font-bold text-xl">
            Android App Download Portal
          </h1>
          <div className="flex items-center space-x-2 ml-auto">
            <h1 className="text-gray-300 text-sm">Welcome, {firstName}</h1>
            <button
              className="bg-gray-800 px-3 py-1 rounded text-gray-200 hover:bg-gray-700 transition duration-300"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        </div>
      </nav>
      <div className="container mx-auto mt-6 px-4">
        <h2 className="text-xl font-bold mb-4 text-white">Available Apps</h2>
        {loading ? (
          <div className="text-white text-center">Loading...</div>
        ) : error ? (
          <div className="text-red-500 text-center">{error}</div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
            {apps.map((app) => {
              const status = getAppStatus(app.id);
              return (
                <div
                  key={app.id}
                  className="bg-gray-800 rounded-lg overflow-hidden shadow-md"
                >
                  {app.app_icon && (
                    <img
                      src={app.app_icon}
                      alt={app.name}
                      className="w-full h-32 object-cover"
                    />
                  )}
                  <div className="p-3">
                    <h3 className="text-lg font-semibold text-white mb-1">{app.name}</h3>
                    <p className="text-gray-400 text-xs mb-2 truncate">{app.description}</p>
                    <div className="flex justify-between items-center">
                      <span className="text-green-500 text-sm font-bold">
                        {app.points_value} Points
                      </span>
                      <div className="flex items-center space-x-2">
                        <span className={`text-xs font-medium ${getStatusColor(status)}`}>
                          {status.replace('_', ' ')}
                        </span>
                        <button
                          className="bg-blue-600 text-white px-2 py-1 rounded text-xs hover:bg-blue-700 transition"
                          onClick={() => handleDownload(app)}
                          disabled={status !== 'NOT_DOWNLOADED'}
                        >
                          Download
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
